import numpy as np

class Trade:
    def __init__(self, stock, threshold=0.5, variance_threshold=0.05):
        self.stock = stock
        self.threshold = threshold
        self.variance_threshold = variance_threshold
        self.profits = []

    def buy(self, predicted_value):
        if predicted_value > self.threshold:
            self.profits.append(self.stock.price)
        else:
            self.profits.append(None)

    def sell(self, predicted_value):
        if predicted_value < self.threshold:
            self.profits.append(self.stock.price)
        else:
            self.profits.append(None)

    def track_variance(self):
        if len(self.profits) >= 5:
            self.variance = np.var([profit for profit in self.profits if profit is not None])
            if self.variance > self.variance_threshold:
                print(f"{self.stock.name} is too risky, selling all shares")
                self.stock.close()

